  const [data, setData] = React.useState();
  React.useEffect(() => {
    const fetchData = async () => {
      const [geoData, ncovData] = await Promise.all([
        fetch(
          'https://gw.alipayobjects.com/os/bmw-prod/e62a2f3b-ea99-4c98-9314-01d7c886263d.json',
        ).then((d) => d.json()),
         // https://lab.isaaclin.cn/nCoV/api/area?latest=1
         fetch(
          'https://gw.alipayobjects.com/os/bmw-prod/55a7dd2e-3fb4-4442-8899-900bb03ee67a.json',
        ).then((d) => d.json()),
      ]);
      setData(joinData(geoData, ncovData.results));
    };
    fetchData();
  }, []);
  return (
    <>
      <MapboxScene
        map={{
          center: [110.19382669582967, 50.258134],
          pitch: 50,
          style: 'blank',
          zoom: 1,
        }}
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
        }}
      >
        {data && [
          <PolygonLayer
            key={'1'}
            options={{
              autoFit: true,
            }}
            source={{
              data,
            }}
            scale={{
              values: {
                confirmedCount: {
                  type: 'quantile',
                },
              },
            }}
            active={{
              option: {
                color: '#ff0',
                mix: .6,
              },
            }}
            color={{
              field: 'confirmedCount',
              values: (count) => {
                return count > 10000
                  ? '#732200'
                  : count > 1000
                  ? '#CC3D00'
                  : count > 500
                  ? '#FF6619'
                  : count > 100
                  ? '#FF9466'
                  : count > 10
                  ? '#FFC1A6'
                  : count > 1
                  ? '#FCE2D7'
                  : 'rgb(255,255,255)';
              },
            }}
            shape={{
              values: 'extrude',
            }}
            size={{
              field: 'confirmedCount',
              values: [0, 1000000, 3000000, 4000000, 5000000],
            }}
            style={{
              opacity: 1,
              pickLight: true,
              heightfixed: true
            }}
          />
        ]}
      </MapboxScene>
    </>
  );
});
ReactDOM.render(<World />, document.getElementById('map'));

