      opacity: 0.8,
      clampLow: false,
      clampHigh: false,
      domain: [ -3000, 10000 ],
      rampColors: {
        colors: [ 'rgb(166,97,26)', 'rgb(223,194,125)', 'rgb(245,245,245)', 'rgb(128,205,193)', 'rgb(1,133,113)' ],
        positions: [ 0, 0.25, 0.5, 0.75, 1.0 ]
