import { Scene, PointLayer, LineLayer } from '@antv/l7';
import { GaodeMap } from '@antv/l7-maps';
import { animate, linear } from 'popmotion';

const path = [
  {
    lng: 168.3984375,
    lat: -4.565473550710278,
