        pageSize: 1,
        // 单页显示结果条数，默认值为20，最大值为50
        city: '北京',
        // 限定查询城市，可以是城市名（中文/中文全拼）、城市编码，默认值为『全国』
        extensions: 'all' // 是否返回公交线路详细信息，默认值为『base』

      }); // 执行公交路线关键字查询

      linesearch.search('536', function (status, result) {
        // 打印状态信息status和结果信息result
        var _result$lineInfo$ = result.lineInfo[0],
            path = _result$lineInfo$.path,
            via_stops = _result$lineInfo$.via_stops;
        var startPoint = [path[0]];
        var endpoint = [path[path.length - 1]];
        var budStopsData = via_stops.map(function (stop) {
          return {
            lng: stop.location.lng,
            lat: stop.location.lat,
            name: stop.name
          };
        });
        var data = [{
          id: '1',
          coord: path.map(function (p) {
            return [p.lng, p.lat];
          })
        }];
        var busLine = new _l.LineLayer({
          blend: 'normal'
        }).source(data, {
          parser: {
            type: 'json',
            coordinates: 'coord'
          }
        }).size(5).shape('line').color('rgb(99, 166, 242)').texture('road').animate({
          interval: 1,
          // 间隔
          duration: 1,
          // 持续时间，延时
          trailLength: 2 // 流线长度

        }).style({
          lineTexture: true,
          iconStep: 25
        });
        scene.addLayer(busLine);
        var startPointLayer = new _l.PointLayer({
          zIndex: 1
        }).source(startPoint, {
          parser: {
            x: 'lng',
            y: 'lat',
            type: 'json'
          }
        }).shape('start').size(20).style({
          offsets: [0, 25]
        });
        scene.addLayer(startPointLayer);
        var endPointLayer = new _l.PointLayer({
          zIndex: 1
        }).source(endpoint, {
          parser: {
            x: 'lng',
            y: 'lat',
            type: 'json'
          }
        }).shape('end').size(25).style({
          offsets: [0, 25]
        });
        scene.addLayer(endPointLayer);
        var busStops = new _l.PointLayer().source(budStopsData, {
          parser: {
            x: 'lng',
            y: 'lat',
            type: 'json'
          }
        }).shape('busStop').size(13).style({
          offsets: [20, 0]
        });
        scene.addLayer(busStops);
        var busStopsName = new _l.PointLayer().source(budStopsData, {
          parser: {
            x: 'lng',
            y: 'lat',
            type: 'json'
          }
        }).shape('name', 'text').size(12).color('#000').style({
          textAnchor: 'left',
          textOffset: [80, 0],
          stroke: '#fff',
          strokeWidth: 1
        });
        scene.addLayer(busStopsName);
      });
    });
  });
});

