      widthSegments: 20,
      heightSegments: 20
    });

    modelData100 = layer.createModelData([], {
      widthSegments: 100,
      heightSegments: 100
    });
  });


  scene.on('zoom', ({ value }) => {
    const zoom = Math.floor(value);
    if (currentZoom !== zoom) {
      if (zoom > 13) {
