      .active(true)
      .animate(true)
      .size(40)
      .color('#ffa842')
      .style({
        opacity: 1,
        offsets: [ 40, 40 ]
      });
    const pointLayer2 = new PointLayer({})
      .source(data, {
        parser: {
          type: 'csv',
          x: 'Longitude',
          y: 'Latitude'
        }
      })
      .shape('circle')
      .active(true)
      .animate(true)
      .size(50)
      .color('#f00')
      .style({
        opacity: 1
      });

    scene.addLayer(pointLayer);
    scene.addLayer(pointLayer2);
  });

