      // .active(true)
      .animate({
        duration: 10,
        interval: 0.2,
        trailLength: 0.05
      })
      .style({
        textureBlend: 'replace',
        lineTexture: true, // 开启线的贴图功能
        iconStep: 10, // 设置贴图纹理的间距
        opacity: 1
      });

    const flyLine2 = new LineLayer()
      .source(flydata, {
