      .size(1)
      // .active(true)
      .style({
        lineType: 'dash',
        dashArray: [ 5, 5 ],
        opacity: 0.5
      });
    scene.addLayer(worldLine);
    scene.addLayer(dotPoint);
    scene.addLayer(flyLine2);
    scene.addLayer(flyLine);
  });
});

