    pitch: 0,
    style: 'light',
    center: [ 113.775374, 28.31067 ],
    zoom: 12
  })
});
scene.on('loaded', () => {
  const drawCircle = new DrawCircle(scene);
  drawCircle.enable();
  drawCircle.on('draw.create', e => {
    console.log(e);
  });
  drawCircle.on('draw.update', e => {
    console.log('update', e);
  });
});

